DROP TABLE IF EXISTS movies.actor;
CREATE TABLE movies.actor (actor_id int, 
  name text,					// name
  headshot_url text,		// headshot
  birth_date text,			// birth date
  trade_mark list<text>,	// trade_mark
  PRIMARY KEY (actor_id)
);

